package com.ibile.features.editmarkerimport android.Manifest.permission.READ_CONTACTSimport android.app.Activityimport android.app.AlertDialogimport android.app.Dialogimport android.content.Intentimport android.content.pm.PackageManager.PERMISSION_GRANTEDimport android.net.Uriimport android.os.Bundleimport android.provider.ContactsContractimport android.provider.MediaStoreimport android.telephony.PhoneNumberUtilsimport android.text.Editableimport android.view.Viewimport android.widget.ArrayAdapterimport android.widget.Toastimport androidx.appcompat.widget.AppCompatTextViewimport androidx.core.net.toFileimport androidx.core.view.setPaddingimport androidx.navigation.fragment.findNavControllerimport com.airbnb.epoxy.EpoxyRecyclerViewimport com.airbnb.mvrx.fragmentViewModelimport com.bumptech.glide.request.RequestOptionsimport com.ibile.MarkerImageItemBindingModel_import com.ibile.Rimport com.ibile.core.*import com.ibile.core.Extensions.dpimport com.ibile.data.database.entities.Markerimport com.ibile.databinding.DialogViewEditMarkerPhoneNumberBindingimport com.ibile.editMarkerDetailsBodyimport com.ibile.editMarkerDetailsHeaderimport com.ibile.features.MarkerImagesPreviewFragmentimport com.ibile.features.MarkerImagesPreviewFragment.Companion.FRAGMENT_TAG_MARKER_IMAGES_PREVIEWimport com.ibile.features.MarkerPhoneNumberActionsDialogDirectionsimport com.ibile.features.editmarker.EditMarkerViewModel.Companion.ARG_MARKER_IDimport com.ibile.utils.EditTextChangeCallbackimport com.ibile.utils.Miscimport com.ibile.utils.extensions.*import com.maltaisn.icondialog.IconDialogimport com.maltaisn.icondialog.IconDialogSettingsimport com.maltaisn.icondialog.data.Iconimport com.maltaisn.icondialog.pack.IconPackimport org.koin.android.ext.android.injectimport petrov.kristiyan.colorpicker.ColorPickerclass EditMarkerDialogFragment : BaseDialogFragment(), IconDialog.Callback,    MarkerImagesPreviewFragment.Callback {    private val callback: Callback        get() = parentFragment as Callback    private val editMarkerViewModel: EditMarkerViewModel by fragmentViewModel()    private val marker: Marker?        get() = editMarkerViewModel.state.marker    private val originalMarker        get() = editMarkerViewModel.state.getMarkerAsync()!!    private var capturedCameraImageUri: Uri? = null    private val iconPack: IconPack by inject()    override val iconDialogIconPack: IconPack?        get() = iconPack    private val iconDialog: IconDialog        get() = childFragmentManager.findFragmentByTag(IconDialog::class.simpleName) as IconDialog?            ?: IconDialog.newInstance(IconDialogSettings { showSelectBtn = false })    private val markerImagesPreviewFragment: MarkerImagesPreviewFragment        get() = childFragmentManager.findFragmentByTag(FRAGMENT_TAG_MARKER_IMAGES_PREVIEW) as? MarkerImagesPreviewFragment            ?: MarkerImagesPreviewFragment()    private var markerImagesPreviewCurrentItem: Int = 0    override val mode: MarkerImagesPreviewFragment.Callback.Mode by lazy {        object : MarkerImagesPreviewFragment.Callback.Mode.Edit(::handleDeleteMarkerImage) {            override val marker: Marker                get() = this@EditMarkerDialogFragment.marker!!            override val initialImageItemIndex: Int                get() = markerImagesPreviewCurrentItem        }    }    private val exitDialog: AlertDialog by lazy {        AlertDialog.Builder(currentContext, R.style.AlertDialog)            .setMessage(getString(R.string.edit_marker_exit_dialog_message))            .setPositiveButton(getString(R.string.text_yes)) { _, _ ->                handleExitDialogPositiveBtnClick()            }            .setNegativeButton(getString(R.string.text_no)) { _, _ ->                handleExitDialogNegativeBtnClick()            }            .setNeutralButton(getString(R.string.text_cancel)) { _, _ ->                handleExitDialogNeutralBtnClick()            }            .create()    }    private fun handleExitDialogPositiveBtnClick() {        editMarkerViewModel.updateMarker(marker!!)    }    private fun handleExitDialogNegativeBtnClick() {        editMarkerViewModel.cleanUpUnsavedMarkerImages()        callback.onComplete(marker!!.id)    }    private fun handleExitDialogNeutralBtnClick() {        exitDialog.dismiss()    }    private fun handleDeleteMarkerImage() {        editMarkerViewModel.editMarker {            val updatedUris = imageUris.filterIndexed { i, uri ->                if (i == mode.initialImageItemIndex && !originalMarker.imageUris.contains(uri))                    editMarkerViewModel.deleteUnsavedMarkerImage(uri)                i != mode.initialImageItemIndex            }            val updatedMarker = copy(imageUris = updatedUris)            markerImagesPreviewFragment.invalidate()            updatedMarker        }    }    private val colorPicker: ColorPicker        get() {            val colorPicker = ColorPicker(requireActivity())                .disableDefaultButtons(true)                .setColumns(5)                .setDefaultColorButton(editMarkerViewModel.state.marker?.color ?: 0)                .setTitle("Choose marker color")                .setRoundColorButton(true)                .setColors(R.array.marker_colors)                .setOnFastChooseColorListener(object : ColorPicker.OnFastChooseColorListener {                    override fun setOnFastChooseColorListener(position: Int, color: Int) {                        editMarkerViewModel.editMarker { copy(color = color) }                    }                    override fun onCancel() {}                })            with(colorPicker.dialogViewLayout) {                setBackgroundColor(context.getResColor(R.color.dark_gray))                val titleView =                    findViewById<AppCompatTextView>(petrov.kristiyan.colorpicker.R.id.title)                titleView.setTextColor(currentContext.getResColor(R.color.white))                titleView.textSize = 12f.dp            }            return colorPicker        }    override fun onCreate(savedInstanceState: Bundle?) {        super.onCreate(savedInstanceState)        setStyle(STYLE_NO_TITLE, android.R.style.Theme_Translucent_NoTitleBar)    }    override fun onViewCreated(view: View, savedInstanceState: Bundle?) {        super.onViewCreated(view, savedInstanceState)        view.findViewById<EpoxyRecyclerView>(R.id.recyclerview).setPadding(4f.dp.toInt())        with(editMarkerViewModel) {            asyncSubscribe(EditMarkerViewModelState::updateMarkerAsync) {                callback.onComplete(marker!!.id)            }            asyncSubscribe(EditMarkerViewModelState::deleteMarkerAsync) {                callback.onComplete(null)            }            if (state.getMarkerAsync() == null) {                editMarkerViewModel.getMarker(state.markerId)            }        }    }    override fun epoxyController(): MvRxEpoxyController = simpleController {        if (marker == null) return@simpleController        val imageItemsCheckBoxIsVisible = editMarkerViewModel.state.selectedImages.isNotEmpty()        // TODO: this should be sticky but there is currently no support for sticky headers in epoxy        editMarkerDetailsHeader {            id("EditMarkerDetailsHeader")            onClickSaveMarker { _ -> handleSaveBtnClick() }            onClickBackBtn { _ -> this@EditMarkerDialogFragment.dialog?.onBackPressed() }            onClickDeleteBtn { _ -> handleDeleteBtnClick() }            saveBtnIsEnabled(marker != originalMarker)        }        editMarkerDetailsBody {            id("EditMarkerDetailsBody")            marker(marker)            titleTextChangeCallback(titleTextChangeHandler)            descriptionTextChangeCallback(descriptionTextChangeHandler)            onClickChangeIcon(handleChangeIconClick)            onClickChangeColor { _ -> colorPicker.show() }            onClickChooseContact { _ -> handleChooseContact() }            onClickEditPhoneNumber { _ -> showEditPhoneNumberDialog() }            onClickCallPhoneNumber { _ -> showPhoneNumberActionsDialog() }            onClickAddImages { _ -> handleAddPhotosBtnClick() }            onClickEditMarkerCoordinatesBtn { _ -> handleEditMarkerCoordinatesBtnClick() }            onClickCopyCoordinates { _ -> handleCopyCoordinatesBtn() }            shareImagesBtnIsVisible(imageItemsCheckBoxIsVisible)            onClickShareImagesBtn { _ -> handleShareMarkerImagesBtnClick() }            val models = buildImageItems(marker!!.imageUris)            models(models)        }    }    private fun buildImageItems(imageUris: List<Uri>)            : List<MarkerImageItemBindingModel_> =        imageUris.mapIndexed { index, uri ->            val progressDrawable = Misc                .createCircularProgressDrawable(centerRadius = 15f, strokeWidth = 3f)            val requestOptions = RequestOptions().centerCrop().placeholder(progressDrawable)            val isSelected = editMarkerViewModel.state.selectedImages.contains(uri)            MarkerImageItemBindingModel_().apply {                id(index)                uri(uri)                dimension(120f)                requestOptions(requestOptions)                isSelected(isSelected)//                checkBoxIsVisible(checkBoxIsVisible)                onClick { _ -> onClickImageItem(uri, index) }//                onClickCheckBox { _ -> handleMarkerImageCheckBoxClick(uri) }                onLongClick { _ ->                    handleMarkerImageLongClick(uri)                    true                }            }        }    private fun handleMarkerImageLongClick(imageUri: Uri) {        val updatedSelectedImages = editMarkerViewModel.state.selectedImages            .toMutableList()            .apply {                add(imageUri)            }.distinct()        editMarkerViewModel.updateState { copy(selectedImages = updatedSelectedImages) }    }    private fun onClickImageItem(uri: Uri, index: Int) {        val inSelectMode = editMarkerViewModel.state.selectedImages.isNotEmpty()        if (!inSelectMode) {            markerImagesPreviewCurrentItem = index            markerImagesPreviewFragment.show(childFragmentManager, FRAGMENT_TAG_MARKER_IMAGES_PREVIEW)        } else {            handleMarkerImageSelect(uri)        }    }    private fun handleMarkerImageSelect(imageUri: Uri) {        val updatedSelectedImages = editMarkerViewModel.state.selectedImages            .toMutableList()            .apply {                if (editMarkerViewModel.state.selectedImages.contains(imageUri)) remove(imageUri)                else add(imageUri)            }.distinct()        editMarkerViewModel.updateState { copy(selectedImages = updatedSelectedImages) }    }    private fun handleShareMarkerImagesBtnClick() {        val selectedImagesUris: List<Uri> = editMarkerViewModel.state.selectedImages            .filter { marker!!.imageUris.contains(it) }            .map { currentContext.getProviderUri(it.toFile()) }        Intent().apply {            action = Intent.ACTION_SEND_MULTIPLE            type = "image/*"            putParcelableArrayListExtra(Intent.EXTRA_STREAM, ArrayList(selectedImagesUris))            startResolvableActivityForResult(                Intent.createChooser(this, "Share images..."),                RC_SHARE_MARKER_PHOTOS            )        }    }    private val titleTextChangeHandler = object : EditTextChangeCallback {        override fun onEditTextChanged(text: Editable) {            editMarkerViewModel.editMarker { copy(name = text.toString()) }        }    }    private val descriptionTextChangeHandler = object : EditTextChangeCallback {        override fun onEditTextChanged(text: Editable) {            editMarkerViewModel.editMarker { copy(description = text.toString()) }        }    }    private val handleChangeIconClick = { _: View ->        iconDialog.show(childFragmentManager, IconDialog::class.simpleName)    }    private fun handleEditMarkerCoordinatesBtnClick() {        callback.onEditMarkerDialogEditCoordinatesBtnClick()    }    private fun handleCopyCoordinatesBtn() {        currentContext.copyTextToClipboard(marker!!.coordinatesInfo)        val message = getString(R.string.text_toast_marker_coordinates_copied)        Toast.makeText(context, message, Toast.LENGTH_SHORT).show()    }    private fun handleSaveBtnClick() {        editMarkerViewModel.updateMarker(marker!!)    }    private fun handleDeleteBtnClick() {        editMarkerViewModel.deleteMarker(marker!!)    }    private fun handleChooseContact() =        runWithPermissions(::chooseContact, READ_CONTACTS, RC_READ_CONTACT_PERMISSION)    private fun chooseContact() {        val intent = Intent(Intent.ACTION_PICK).apply {            type = ContactsContract.CommonDataKinds.Phone.CONTENT_TYPE        }        startResolvableActivityForResult(intent, RC_CHOOSE_CONTACT)    }    private fun showEditPhoneNumberDialog() {        val binding = DialogViewEditMarkerPhoneNumberBinding.inflate(layoutInflater)        binding.etEditMarkerPhoneNumber.setText(marker?.phoneNumber)        AlertDialog.Builder(currentContext, R.style.AlertDialog)            .setTitle(getString(R.string.title_edit_phone_number))            .setView(binding.root)            .setPositiveButton(getString(R.string.text_ok)) { _, _ ->                val number = binding.etEditMarkerPhoneNumber.text.toString()                editMarkerViewModel.editMarker { copy(phoneNumber = number) }            }            .setNegativeButton(getString(R.string.text_cancel)) { _, _ -> }            .create()            .show()    }    private fun showPhoneNumberActionsDialog() {        marker?.phoneNumber?.let {            val direction = MarkerPhoneNumberActionsDialogDirections                .actionGlobalMarkerPhoneNumberActionsDialog(it)            findNavController().navigate(direction)        }    }    private fun handleAddPhotosBtnClick() {        val optionItemsAdapter = ArrayAdapter(            currentContext,            R.layout.dialog_edit_marker_add_photos_options_list_item,            R.id.tv_add_photo_option,            listOf("From camera", "From gallery")        )        AlertDialog.Builder(currentContext, R.style.AlertDialog)            .setTitle("Add image")            .setNegativeButton(getString(R.string.text_cancel)) { _, _ -> }            .setAdapter(optionItemsAdapter) { _, i ->                when (i) {                    0 -> captureImageFromCamera()                    1 -> selectPhotosFromSystem()                }            }            .create()            .show()    }    private fun selectPhotosFromSystem() {        val intent = Intent(Intent.ACTION_OPEN_DOCUMENT).apply {            type = "image/*"            addCategory(Intent.CATEGORY_OPENABLE)            putExtra(Intent.EXTRA_ALLOW_MULTIPLE, true)            putExtra(Intent.EXTRA_LOCAL_ONLY, true)        }        startResolvableActivityForResult(intent, RC_CHOOSE_IMAGES)    }    private fun captureImageFromCamera() {        val intent = Intent(MediaStore.ACTION_IMAGE_CAPTURE)        if (intent.resolveActivity(currentContext.packageManager) == null) return        currentContext.createImageFile()?.let {            capturedCameraImageUri = Uri.fromFile(it)            val uri = currentContext.getProviderUri(it)            intent.putExtra(MediaStore.EXTRA_OUTPUT, uri)            startActivityForResult(intent, RC_TAKE_PHOTO)        }    }    override fun onRequestPermissionsResult(        requestCode: Int,        permissions: Array<out String>,        grantResults: IntArray    ) {        if (grantResults.isEmpty() || grantResults[0] != PERMISSION_GRANTED) return        when (requestCode) {            RC_READ_CONTACT_PERMISSION -> chooseContact()        }    }    override fun onActivityResult(requestCode: Int, resultCode: Int, data: Intent?) {        when {            requestCode == RC_CHOOSE_CONTACT && resultCode == Activity.RESULT_OK ->                onSelectContact(data)            requestCode == RC_CHOOSE_IMAGES && resultCode == Activity.RESULT_OK ->                onChooseImages(data)            requestCode == RC_TAKE_PHOTO && resultCode == Activity.RESULT_OK -> {                editMarkerViewModel.editMarker {                    val uris = imageUris.toMutableList().apply { add(capturedCameraImageUri!!) }                    copy(imageUris = uris)                }            }            requestCode == RC_SHARE_MARKER_PHOTOS -> editMarkerViewModel.updateState {                copy(selectedImages = arrayListOf())            }        }    }    private fun onSelectContact(data: Intent?) {        val number = PhoneNumberUtils.getNumberFromIntent(data, currentContext)        editMarkerViewModel.editMarker { copy(phoneNumber = number) }    }    private fun onChooseImages(data: Intent?) {        val uris = mutableListOf<Uri>()        data?.clipData            ?.let { for (i in 0 until it.itemCount) uris.add(it.getItemAt(i).uri) }            ?: data?.data?.let { uris.add(it) }        editMarkerViewModel.onChooseMarkerImages(uris)    }    override fun onIconDialogIconsSelected(dialog: IconDialog, icons: List<Icon>) {        val selectedIcon = icons[0]        editMarkerViewModel.editMarker { copy(icon = Marker.Icon(selectedIcon.id)) }    }    override fun onCreateDialog(savedInstanceState: Bundle?): Dialog {        return object : Dialog(requireActivity(), theme) {            override fun onBackPressed() {                if (marker != originalMarker) {                    exitDialog.show()                    return                }                callback.onComplete(editMarkerViewModel.state.markerId)            }        }    }    companion object {        const val RC_CHOOSE_CONTACT = 1001        const val RC_READ_CONTACT_PERMISSION = 1002        const val RC_CHOOSE_IMAGES = 1003        const val RC_TAKE_PHOTO = 1004        const val RC_SHARE_MARKER_PHOTOS = 1005        fun newInstance(markerId: Long): EditMarkerDialogFragment {            val args = Bundle()            args.putLong(ARG_MARKER_ID, markerId)            val fragment = EditMarkerDialogFragment()            fragment.arguments = args            return fragment        }    }    interface Callback {        fun onEditMarkerDialogEditCoordinatesBtnClick()        fun onComplete(markerId: Long?)    }}